const MAX_ALERTS = 5;

function processAlerts(data,type) {
    var messageData = flow.get("alerts_"+type)||[];
    if (data.length>MAX_ALERTS) {
        messageData = data.slice(0,MAX_ALERTS);
    } else {
        if ((data.length+messageData.length)>MAX_ALERTS) {
            messageData = messageData.slice(0, MAX_ALERTS - data.length);
            for (const val in data) {
                messageData.unshift(val);
            }
        }
    }
    flow.set("alerts_"+type,messageData);
    return messageData;
}

